// ุชุจุฏูู ุงููุบุฉ
function toggleLanguage() {
    currentLang = currentLang === 'ar' ? 'en' : 'ar';
    localStorage.setItem('language', currentLang);
    applyLanguage(currentLang);
}

// ุชุทุจูู ุงููุบุฉ
function applyLanguage(lang) {
    document.documentElement.setAttribute('lang', lang);
    document.documentElement.setAttribute('dir', lang === 'ar' ? 'rtl' : 'ltr');
    
    const elements = document.querySelectorAll('[data-translate]');
    elements.forEach(element => {
        const key = element.getAttribute('data-translate');
        if (translations[lang][key]) {
            if (element.tagName === 'INPUT' || element.tagName === 'TEXTAREA') {
                element.placeholder = translations[lang][key];
            } else {
                // ุฅุถุงูุฉ ุงูููุทุชูู ููุนูุงุตุฑ <strong> ูู ูุณู ุงููุนูููุงุช ุงูุณุฑูุนุฉ
                if (element.tagName === 'STRONG' && 
                    element.closest('.info-box')) {
                    const colon = lang === 'ar' ? ':' : ':';
                    element.textContent = translations[lang][key] + colon;
                } else {
                    element.textContent = translations[lang][key];
                }
            }
        }
    });
    
    // ุชุญุฏูุซ ุฒุฑ ุงููุบุฉ
    const langBtn = document.querySelector('.lang-btn');
    if (langBtn) {
        langBtn.textContent = lang === 'ar' ? 'English' : 'ุงูุนุฑุจูุฉ';
    }
    
    // ุชุญุฏูุซ ุนููุงู ุฒุฑ ุงููุถุน ุงููููู
    const darkModeBtn = document.querySelector('.dark-mode-btn');
    if (darkModeBtn) {
        darkModeBtn.setAttribute('title', lang === 'ar' ? 'ุชุจุฏูู ุงููุถุน ุงููููู' : 'Toggle Dark Mode');
    }
    
    applyLanguageStyles(lang);
}

